// src/components/CreateDoctor.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateDoctor.css';

function CreateDoctor({ addDoctor }) {
  const [doctor, setDoctor] = useState({
    id: '',
    name: '',
    speciality: '',
    location: '',
    hospital: '',
    email: '',
    phone: '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addDoctor(doctor); // Adds the new doctor to the list
    navigate('/doctorlist'); // Redirects back to the doctor list
  };

  return (
    <div className="create-doctor">
      <h2>Create Doctor</h2>
      <form onSubmit={handleSubmit} className="doctor-form">
        <label>Doctor ID: <input type="text" name="id" value={doctor.id} onChange={handleChange} required /></label>
        <label>Name: <input type="text" name="name" value={doctor.name} onChange={handleChange} required /></label>
        <label>Speciality: <input type="text" name="speciality" value={doctor.speciality} onChange={handleChange} required /></label>
        <label>Location: <input type="text" name="location" value={doctor.location} onChange={handleChange} required /></label>
        <label>Hospital: <input type="text" name="hospital" value={doctor.hospital} onChange={handleChange} required /></label>
        <label>Email: <input type="email" name="email" value={doctor.email} onChange={handleChange} required /></label>
        <label>Phone: <input type="tel" name="phone" value={doctor.phone} onChange={handleChange} required /></label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default CreateDoctor;
