import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Ensure Axios is installed and imported
import './Login.css';

function Login() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const [message, setMessage] = useState(''); // For displaying login status
  const navigate = useNavigate();

  // Handle forgot password logic (for now, just logging the action)
  const handleForgotPassword = () => {
    alert('Forgot password functionality not implemented yet.');
    // Replace with actual logic for sending a reset link or emailing the user
  };

  // Handle login submission
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8082/loginUser', {
        userId,
        password,
        userType,
      });

      if (response.data.success) {
        setMessage('Login successful!');
        console.log('Login response:', response.data);

        // Redirect user to the dashboard
        navigate('/dashboard');
      } else {
        // Backend response indicates failure
        setMessage(response.data.message || 'Login failed. Please try again.');
      }
    } catch (error) {
      // Handle errors (network, server, etc.)
      const errorMessage = error.response?.data?.message || 'An error occurred during login.';
      setMessage(errorMessage);
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        {message && <p className="message">{message}</p>}
        <label htmlFor="userId">Email</label>
        <input
          type="email"
          id="userId"
          name="userId"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <label htmlFor="userType">Password</label>
        <input
          type="text"
          id="userType"
          name="userType"
          value={userType}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password button */}
        <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
          Forgot Password?
        </button>
      </form>
    </div>
  );
}

export default Login;
