import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

function Login() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleForgotPassword = () => {
    alert('Forgot password functionality not implemented yet.');
    // Replace this with actual logic for sending a reset link or emailing the user
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    // Create login data object
    const loginData = { userId, password, userType };
    console.log('Login data:', loginData);

    try {
      // Sending POST request to the backend
      const response = await axios.post('http://localhost:8082/loginUser', loginData);

      if (response.data.success) {
        console.log('Login successful:', response.data);
        alert('Login successful!');
        navigate('/dashboard'); // Redirect to the dashboard
      } else {
        console.log('Login failed:', response.data.message);
        setMessage(response.data.message || 'Invalid credentials!');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setMessage('An error occurred during login. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="userId">Email</label>
        <input
          type="email"
          id="userId"
          name="userId"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <select
          id="userType"
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
          required
        >
          <option value="">Select Role</option>
          <option value="Patient">Patient</option>
          <option value="Doctor">Doctor</option>
        </select>
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password button */}
        <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
          Forgot Password?
        </button>

        {/* Display error message */}
        {message && <p className="error-message">{message}</p>}
      </form>
    </div>
  );
}

export default Login;
