import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const navigate = useNavigate();

  const handleForgotPassword = () => {
    alert('Forgot password functionality not implemented yet.');
    // You can replace this with actual logic for sending a reset link or emailing the user
  };

  const handleLogin = (e) => {
    e.preventDefault();

    // Create a login object and print it to the console
    const loginData = { userId, password,userType };
    console.log('Login data:', loginData);

    // For demonstration purposes, you can add actual login logic here
    if (userId === 'admin@gmail.com' && password === 'admin123' && userType === '' ) {
      console.log('Admin login successful:', loginData);
      alert('Admin login successful!');
      navigate('/dashboard'); // Redirect to the admin dashboard
    } else {
      console.log('Login failed. Invalid credentials:', loginData);
      alert('Invalid userId or password!');
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        <label htmlFor="userId">Email</label>
        <input
          type="email"
          id="userId"
          name="userId"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
         <select
          id="userType"
          value={userType}
          onChange={(e) => setUserType(e.target.value)}
          required
        >
          <option value="">Select Role</option>
          <option value="Patient">Patient</option>
          <option value="Doctor">Doctor</option>
        </select>
        <button type="submit">Login</button>
        <p>New user? <a href="/register">Register here</a></p>

        {/* Forgot Password button */}
        <button type="button" onClick={handleForgotPassword} className="forgot-password-button">
          Forgot Password?
        </button>
      </form>
    </div>
  );
}

export default Login;
